select.if:
select AF.TITLE, AF.SVCGRP, AF.REGDATE, AF.SOLUTION, AF.DATA_ID, AF.URL
from (select rownum as num, NO, TITLE, SVCGRP, REGDATE, SOLUTION, DATA_ID, USER_ID, URL from AT_FAVORITE) AF
where AF.USER_ID = #{userId}
and AF.num  >  #{page}
and AF.num  <=  #{count}

select AF.TITLE, AF.SVCGRP, AF.REGDATE, AF.SOLUTION, AF.DATA_ID, AF.URL
from AT_FAVORITE AF
where AF.USER_ID = #{userId}


        order by AF.REGDATE desc


        order by AF.REGDATE


limit #{page} , #{count}

mapper.select:
select NO
from AT_FAVORITE
where USER_ID = #{userId}
and SVCGRP = #{svcgrp}
and DATA_ID = #{dataId}
and SOLUTION = 'rss'

mapper.insert:

    select SEQ_AT_FAVORITE.CURRVAL from dual
    select LAST_INSERT_ID()

insert into AT_FAVORITE (
    NO,
    TITLE, SVCGRP, SOLUTION, DATA_ID, URL, USER_ID
)
values(
    SEQ_AT_FAVORITE.NEXTVAL,
    #{title, jdbcType=VARCHAR}, #{svcgrp, jdbcType=VARCHAR}, #{solution, jdbcType=VARCHAR}, #{dataId, jdbcType=VARCHAR}, #{url, jdbcType=VARCHAR}, #{userId, jdbcType=VARCHAR}
)

mapper.delete:
delete from AT_FAVORITE
where USER_ID = #{userId}
and SVCGRP = #{svcgrp}
and DATA_ID = #{dataId}
and SOLUTION = 'rss'
